import React from 'react' 
import {Card, Form, Button, Col} from 'react-bootstrap'
import SearchTable from './SearchTable'
import Results from "./Results"
import { withRouter} from 'react-router-dom'; 


class AddProduct extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
            productName: '',
        }

        this.productChange = this.productChange.bind(this);
        this.submitProduct = this.submitProduct.bind(this);
        this.clearField = this.clearField.bind(this);
    }

    submitProduct(productName) {

        //<SearchTable productname={this.state.productName} />
        //<Results productName={e.target.productName}/>
        this.props.history.push('/Results/${name}');
    }

    productChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    clearField = () => {
        this.setState({
            productID: '',
            productName: '',
            productPrice: '',
            productCategory: '',
            productDescription: ''
        })
    }

    render() {
        return(
            <div>
                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Body>
                        <Form onSubmit={this.submitProduct} controlId="addForm">
                            <Form.Row>
                            <Form.Group as={Col} controlId="formGroupName">
                                <Form.Control required type="text" className={"bg-dark text-white"} placeholder="Enter Product Name" name="productName" value={this.state.productName} onChange={this.productChange}/>
                            </Form.Group>
                            </Form.Row>
                        </Form>
                    </Card.Body>
                    <Card.Footer style={{"textAlign":"right"}}>
                        <Button size="sm" variant="success" type="submit" title="button1" onClick={this.submitProduct(e.productName)} >Search</Button>{' '}
                        <Button controlId="button" size="sm" variant="primary" type="reset" onClick={this.clearField}>Reset</Button>
                    </Card.Footer>
                </Card>

            </div>
        )
    }
}

export default withRouter(AddProduct);
