import React from 'react' 
import {Card, Form, Button, Col, Table} from 'react-bootstrap'
import NavCustomer from './NavCustomer'




class Results extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
            productName: this.props.match.params.productName,
            products: []
        }
        console.log(props.productName)
        this.productChange = this.productChange.bind(this);
        this.submitProduct = this.submitProduct.bind(this);
        this.clearField = this.clearField.bind(this);
        this.submitProduct
    }

    submitProduct(e) {
        e.preventDefault();
        //fetch('http://localhost:8086/Application/Customer/product/search/'+this.state.productName)
        fetch('http://localhost:8086/ProductReview/Customer/product/search/Note')
        .then(res => res.json())
        .then(json => {
            this.setState({
                products: json
            })
        })
    }

    productChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    clearField = () => {
        this.setState({
            productID: '',
            productName: '',
            productPrice: '',
            productCategory: '',
            productDescription: ''
        })
    }
  
    render() {
        return(
            <div>
                <NavCustomer />
                
                <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header>Products Found</Card.Header>
                    <Card.Body>
                        <Table bordered hover striped variant="dark">
                            <thead>
                                <tr>
                                    <th>Product ID</th>
                                    <th>Product Name</th>
                                    <th>Product Price</th>
                                    <th>Product Category</th>
                                    <th>Product Description</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.products.length === 0 ?
                                    <tr align="center">
                                        <td colSpan="5">{this.state.products.length} Products Available</td>
                                    </tr> :
                                    this.state.products.map((product) => (
                                      <tr key={product.productID}>
                                          <td>{product.productID}</td>
                                          <td>{product.productName}</td>
                                          <td>{product.productPrice}</td>
                                          <td>{product.productCategory}</td>
                                          <td>{product.productDescription}</td>
                                      </tr>  
                                    ))
                                }
                            </tbody>
                        </Table>
                    </Card.Body>
            </Card>
            </div>
        )
    }
}

export default Results;
